# [2156] Find Substring With Given Hash Value
#

# @lc code=start
class Solution:
    def subStrHash(self, s: str, power: int, modulo: int, k: int, hashValue: int) -> str:
        def val(c):
            return ord(c) - ord("a") + 1

        def hash(s, p, m):
            sum_powers = 0
            for i, c in enumerate(s):
                sum_powers += val(c) * (p**i)
            return sum_powers % m

        powers = []

        for i in range(k):
            powers.append(power**i)

        for i in range(0, len(s) - k + 1):
            if hash(s[i : i + k], power, modulo) == hashValue:
                return s[i : i + k]

        return ""


# @lc code=end

sol = Solution()
res = sol.subStrHash(
    "",
    71717,
    94536,
    1149,
    39999,
)
print(res)
